INSERT INTO `zs_article` VALUES (31,25,'潍坊高新技术产业开发区招商促进局','','','','','','','<p class=\"p19\" style=\"background-color:#FFFFFF;text-indent:2em;\">\r\n\t<span style=\"line-height:1.5;\"> </span> \r\n</p>\r\n<p style=\"text-indent:2em;\">\r\n\t<span style=\"font-family:SimSun;font-size:16px;line-height:2;\">一、潍坊高新技术产业开发区招商促进局</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n\t<span style=\"font-family:SimSun;font-size:16px;line-height:2;\">潍坊高新技术产业开发区招商促进局（挂招商与项目促进中心牌子）为管委会直属事业单位，副县级规格，负责全区招商引资工作的综合协调、指导、督查及专业招商等工作。内设</span><span style=\"font-family:SimSun;font-size:16px;line-height:2;\">4</span><span style=\"font-family:SimSun;font-size:16px;line-height:2;\">个正科级科室，分别为：</span><span style=\"font-family:SimSun;font-size:16px;line-height:2;\">1</span><span style=\"font-family:SimSun;font-size:16px;line-height:2;\">、综合科，负责综合协调、人事、党建、文秘档案、文稿起草、财务、会务接待、后勤管理、督察督办及招商引资信息调研、咨询、统计等工作。</span><span style=\"font-family:SimSun;font-size:16px;line-height:2;\">2、招商一科、招商二科、招商三科，分别负责境内、亚太、欧美地区招商引资工作的综合协调、指导、督查、专业招商及招商宣传和项目推介等工作。</span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n\t<span style=\"font-family:SimSun;font-size:16px;line-height:2;\">　　联系方式：</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n\t<span style=\"font-family:SimSun;font-size:16px;line-height:2;\">　　综合科：</span><span style=\"font-family:SimSun;font-size:16px;line-height:2;\">&nbsp; 0536-8786838</span><span style=\"font-family:SimSun;font-size:16px;line-height:2;\">　 </span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n\t<span style=\"font-family:SimSun;font-size:16px;line-height:2;\">　　招商一科：</span><span style=\"font-family:SimSun;font-size:16px;line-height:2;\">0536-8867319</span><span style=\"font-family:SimSun;font-size:16px;line-height:2;\">　　</span><span></span> \r\n</p>\r\n<p class=\"MsoNormal\" style=\"text-indent:2em;\">\r\n\t<span style=\"font-family:SimSun;font-size:16px;line-height:2;\">　　招商二